/* eslint-disable @typescript-eslint/interface-name-prefix */
import React, { useEffect, useState } from 'react';
import { Formik, Form, Field, FieldArray } from 'formik';
import { TextField, MenuItem, IconButton, InputAdornment, Grid, Button } from '@material-ui/core';
import CancelRoundedIcon from '@material-ui/icons/CancelRounded';
import RemoveCircleRoundedIcon from '@material-ui/icons/RemoveCircleRounded';
import AddCircleRoundedIcon from '@material-ui/icons/AddCircleRounded';
interface IInitialValues {
  name: {
    first: string;
    last: string;
    middle: string;
  };
  gender: string;
  emails: Array<string>;
  phones: Array<string>;
  dob: string;
  maritalStatus: string;
  aadharNo: string;
  panid: string;
}
interface IOption {
  code: string;
  label: string;
}
const genderOption: Array<IOption> = [
  { code: 'f', label: 'Female' },
  { code: 'm', label: 'Male' },
];
const maritalStatusOption: Array<IOption> = [
  { code: 's', label: 'Single' },
  { code: 'm', label: 'Married' },
];
const hSubmit = async function (
  values: IInitialValues,
  setSubmitting: (isSubmitting: boolean) => void,
  userId: string | undefined,
): Promise<void> {
  if (!Object.keys(values).length || !userId) {
    setSubmitting(false);
    return;
  }
  try {
    const options: {
      method: string;
      headers: {
        'Content-Type': string;
      };
      body: string;
    } = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(values),
    };
    await fetch(`http://localhost:8080/api/user/edit/${userId}`, options);
  } catch (error) {}
};
const EditUserProfile: React.FC = function () {
  const [userId, setUserId] = useState<undefined | string>(window.localStorage.getItem('HS_userId') || undefined);
  useEffect(() => {
    if (userId) {
      const fetchData = async function (): Promise<void> {
        await fetch(`http://localhost:8080/api/user/profile/${userId}`, {
          method: 'GET',
          headers: { 'Content-Type': 'application/json' },
        });
      };
      fetchData();
    }
  }, [userId]);
  const initialValues: IInitialValues = {
    name: {
      first: '',
      last: '',
      middle: '',
    },
    gender: '',
    emails: [''],
    phones: [''],
    dob: '',
    maritalStatus: '',
    aadharNo: '',
    panid: '',
  };
  return (
    <Formik
      initialValues={initialValues}
      onSubmit={(values, { setSubmitting }) => hSubmit(values, setSubmitting, userId)}
    >
      {({ values, setFieldValue, dirty, isSubmitting }) => (
        <Form>
          <Grid container direction="column" spacing={3} style={{ marginTop: '2rem' }}>
            <Grid item container justify="center" spacing={3} xs={12}>
              <Grid item xs={3}>
                <Field type="text" name="name.first" label="First Name :" variant="outlined" fullWidth as={TextField} />
              </Grid>
              <Grid item xs={3}>
                <Field
                  type="text"
                  name="name.middle"
                  label="Middle Name :"
                  variant="outlined"
                  fullWidth
                  as={TextField}
                />
              </Grid>
              <Grid item xs={3}>
                <Field type="text" name="name.last" label="Last Name :" variant="outlined" fullWidth as={TextField} />
              </Grid>
            </Grid>
            <Grid item container justify="center" spacing={3} xs={12}>
              <Grid item xs={3}>
                <Field
                  select
                  name="gender"
                  label="Gender :"
                  variant="outlined"
                  fullWidth
                  as={TextField}
                  InputProps={{
                    endAdornment: (
                      <InputAdornment position="end">
                        <IconButton
                          onClick={(): void => {
                            setFieldValue('gender', '');
                          }}
                        >
                          <CancelRoundedIcon color="primary" />
                        </IconButton>
                      </InputAdornment>
                    ),
                  }}
                >
                  {genderOption.map((g) => {
                    return (
                      <MenuItem key={g.code} value={g.code}>
                        {g.label}
                      </MenuItem>
                    );
                  })}
                </Field>
              </Grid>
              <Grid item xs={3}>
                <Field
                  select
                  name="maritalStatus"
                  label="Marital Status :"
                  variant="outlined"
                  fullWidth
                  as={TextField}
                  InputProps={{
                    endAdornment: (
                      <InputAdornment position="end">
                        <IconButton
                          onClick={(): void => {
                            setFieldValue('maritalStatus', '');
                          }}
                        >
                          <CancelRoundedIcon color="primary" />
                        </IconButton>
                      </InputAdornment>
                    ),
                  }}
                >
                  {maritalStatusOption.map((g) => {
                    return (
                      <MenuItem key={g.code} value={g.code}>
                        {g.label}
                      </MenuItem>
                    );
                  })}
                </Field>
              </Grid>
              <Grid item xs={3}>
                <Field name="aadharNo" type="text" label="Aadhar No :" variant="outlined" fullWidth as={TextField} />
              </Grid>
            </Grid>
            <Grid item container justify="center" spacing={3} xs={12}>
              <Grid item xs={3}>
                <Field type="date" name="dob" label="Dob: " variant="outlined" fullWidth as={TextField} />
              </Grid>
              <Grid item xs={3}>
                <FieldArray
                  name="emails"
                  render={({ push, remove }) => {
                    return values.emails.map((el, ind) => (
                      <Field
                        key={ind}
                        type="email"
                        label={ind === 0 ? 'Emails : ' : ''}
                        name={`emails.${ind}`}
                        variant="outlined"
                        fullWidth
                        as={TextField}
                        InputProps={{
                          endAdornment: (
                            <InputAdornment position="end">
                              <IconButton
                                onClick={(): void => {
                                  push('');
                                }}
                              >
                                <AddCircleRoundedIcon color="primary" />
                              </IconButton>
                              {values.emails.length > 1 && (
                                <IconButton
                                  onClick={(): void => {
                                    remove(ind);
                                  }}
                                >
                                  <RemoveCircleRoundedIcon color="primary" />
                                </IconButton>
                              )}
                            </InputAdornment>
                          ),
                        }}
                      />
                    ));
                  }}
                />
              </Grid>
              <Grid item xs={3}>
                <FieldArray
                  name="phones"
                  render={({ push, remove }) => {
                    return values.phones.map((el, ind) => (
                      <Field
                        key={ind}
                        type="text"
                        label={ind === 0 ? 'Phones : ' : ''}
                        name={`phones.${ind}`}
                        variant="outlined"
                        fullWidth
                        as={TextField}
                        InputProps={{
                          endAdornment: (
                            <InputAdornment position="end">
                              <IconButton
                                onClick={(): void => {
                                  push('');
                                }}
                              >
                                <AddCircleRoundedIcon color="primary" />
                              </IconButton>
                              {values.phones.length > 1 && (
                                <IconButton
                                  onClick={(): void => {
                                    remove(ind);
                                  }}
                                >
                                  <RemoveCircleRoundedIcon color="primary" />
                                </IconButton>
                              )}
                            </InputAdornment>
                          ),
                        }}
                      />
                    ));
                  }}
                />
              </Grid>
            </Grid>
            <Grid item container justify="center" spacing={3} xs={12}>
              <Grid item xs={3}>
                <Field name="aadharNo" type="text" label="Aadhar No :" variant="outlined" fullWidth as={TextField} />
              </Grid>
            </Grid>
            <Grid item container justify="center" spacing={3} xs={12}>
              <Button type="submit" variant="contained" color="primary" disabled={!dirty || isSubmitting}>
                Submit
              </Button>
            </Grid>
          </Grid>
          <pre>{JSON.stringify(values)}</pre>
        </Form>
      )}
    </Formik>
  );
};
export default EditUserProfile;
