/* eslint-disable @typescript-eslint/interface-name-prefix */
import React from 'react';
import { Formik, Form, Field, FieldArray } from 'formik';
import { TextField, MenuItem, IconButton, InputAdornment, Grid } from '@material-ui/core';
import ClearIcon from '@material-ui/icons/Clear';
import RemoveCircleRoundedIcon from '@material-ui/icons/RemoveCircleRounded';
import AddCircleRoundedIcon from '@material-ui/icons/AddCircleRounded';
interface IInitialValues {
  name: {
    first: string;
    last: string;
    middle: string;
  };
  gender: string;
  emails: Array<string>;
  phones: Array<string>;
  dob: string;
  maritalStatus: string;
  aadharNo: string;
  panid: string;
}
interface IOption {
  code: string;
  label: string;
}
const genderOption: Array<IOption> = [
  { code: 'f', label: 'Female' },
  { code: 'm', label: 'Male' },
];
const maritalStatusOption: Array<IOption> = [
  { code: 's', label: 'Single' },
  { code: 'm', label: 'Married' },
];
const EditUserProfile: React.FC = function () {
  const initialValues: IInitialValues = {
    name: {
      first: '',
      last: '',
      middle: '',
    },
    gender: '',
    emails: [''],
    phones: [''],
    dob: '',
    maritalStatus: '',
    aadharNo: '',
    panid: '',
  };
  return (
    <Formik initialValues={initialValues} onSubmit={() => console.log('submitted')}>
      {({ values, setSubmitting, setFieldValue }) => (
        <Form>
          <Grid container direction="column" spacing={3}>
            <Grid item container justify="center" spacing={3} xs={12}>
              <Grid item xs={3}>
                <Field type="text" name="name.first" label="First Name :" variant="outlined" fullWidth as={TextField} />
              </Grid>
              <Grid item xs={3}>
                <Field
                  type="text"
                  name="name.middle"
                  label="Middle Name :"
                  variant="outlined"
                  fullWidth
                  as={TextField}
                />
              </Grid>
              <Grid item xs={3}>
                <Field type="text" name="name.last" label="Last Name :" variant="outlined" fullWidth as={TextField} />
              </Grid>
            </Grid>
            <Grid item container justify="center" spacing={3} xs={12}>
              <Grid item xs={3}>
                <Field
                  select
                  name="gender"
                  label="Gender :"
                  variant="outlined"
                  fullWidth
                  as={TextField}
                  InputProps={{
                    endAdornment: (
                      <InputAdornment position="end">
                        <IconButton
                          onClick={(): void => {
                            setFieldValue('gender', '');
                          }}
                        >
                          <ClearIcon />
                        </IconButton>
                      </InputAdornment>
                    ),
                  }}
                >
                  {genderOption.map((g) => {
                    return (
                      <MenuItem key={g.code} value={g.code}>
                        {g.label}
                      </MenuItem>
                    );
                  })}
                </Field>
              </Grid>
              <Grid item xs={3}>
                <Field
                  select
                  name="maritalStatus"
                  label="Marital Status :"
                  variant="outlined"
                  fullWidth
                  as={TextField}
                  InputProps={{
                    endAdornment: (
                      <InputAdornment position="end">
                        <IconButton
                          onClick={(): void => {
                            setFieldValue('gender', '');
                          }}
                        >
                          <ClearIcon />
                        </IconButton>
                      </InputAdornment>
                    ),
                  }}
                >
                  {maritalStatusOption.map((g) => {
                    return (
                      <MenuItem key={g.code} value={g.code}>
                        {g.label}
                      </MenuItem>
                    );
                  })}
                </Field>
              </Grid>
              <Grid item xs={3}>
                <Field name="aadharNo" type="text" label="Aadhar No :" variant="outlined" fullWidth as={TextField} />
              </Grid>
            </Grid>
            <Grid item container justify="center" spacing={3} xs={12}>
              <Grid item xs={3}>
                <Field type="date" name="dob" label="Dob: " variant="outlined" fullWidth as={TextField} />
              </Grid>
              <Grid item xs={3}>
                <FieldArray
                  name="emails"
                  render={({ push, remove }) => {
                    return values.emails.map((el, ind) => (
                      <Field
                        key={ind}
                        type="email"
                        label={ind === 0 ? 'Emails : ' : ''}
                        name={`emails.${ind}`}
                        variant="outlined"
                        fullWidth
                        as={TextField}
                        InputProps={{
                          endAdornment: (
                            <InputAdornment position="end">
                              <IconButton
                                onClick={(): void => {
                                  push('');
                                }}
                              >
                                <AddCircleRoundedIcon color="primary" />
                              </IconButton>
                              {values.emails.length > 1 && (
                                <IconButton
                                  onClick={(): void => {
                                    remove(ind);
                                  }}
                                >
                                  <RemoveCircleRoundedIcon color="primary" />
                                </IconButton>
                              )}
                            </InputAdornment>
                          ),
                        }}
                      />
                    ));
                  }}
                />
              </Grid>
              <Grid item xs={3}>
                <FieldArray
                  name="phones"
                  render={({ push, remove }) => {
                    return values.phones.map((el, ind) => (
                      <Field
                        key={ind}
                        type="text"
                        label={ind === 0 ? 'Phones : ' : ''}
                        name={`phones.${ind}`}
                        variant="outlined"
                        fullWidth
                        as={TextField}
                        InputProps={{
                          endAdornment: (
                            <InputAdornment position="end">
                              <IconButton
                                onClick={(): void => {
                                  push('');
                                }}
                              >
                                <AddCircleRoundedIcon color="primary" />
                              </IconButton>
                              {values.phones.length > 1 && (
                                <IconButton
                                  onClick={(): void => {
                                    remove(ind);
                                  }}
                                >
                                  <RemoveCircleRoundedIcon color="primary" />
                                </IconButton>
                              )}
                            </InputAdornment>
                          ),
                        }}
                      />
                    ));
                  }}
                />
              </Grid>
            </Grid>
          </Grid>
          <pre>{JSON.stringify(values)}</pre>
        </Form>
      )}
    </Formik>
  );
};
export default EditUserProfile;
