/* eslint-disable @typescript-eslint/interface-name-prefix */
import React from 'react';
import { Formik, Form, Field, FieldArray } from 'formik';
import { TextField, MenuItem, IconButton, InputAdornment } from '@material-ui/core';
import ClearIcon from '@material-ui/icons/Clear';
import AddCircleOutlineIcon from '@material-ui/icons/AddCircleOutline';
interface IInitialValues {
  name: {
    first: string;
    last: string;
    middle: string;
  };
  gender: string;
  emails: Array<string>;
  phones: Array<string>;
  dob: string;
  maritalStatus: string;
}
interface IOption {
  code: string;
  label: string;
}
const genderOption: Array<IOption> = [
  { code: 'f', label: 'Female' },
  { code: 'm', label: 'Male' },
];
const maritalStatusOption: Array<IOption> = [
  { code: 's', label: 'Single' },
  { code: 'm', label: 'Married' },
];
const EditUserProfile: React.FC = function () {
  const initialValues: IInitialValues = {
    name: {
      first: '',
      last: '',
      middle: '',
    },
    gender: '',
    emails: [''],
    phones: [''],
    dob: '',
    maritalStatus: '',
  };
  return (
    <Formik initialValues={initialValues} onSubmit={() => console.log('submitted')}>
      {({ values, setSubmitting, setFieldValue }) => (
        <Form>
          <Field type="text" name="name.first" label="First Name :" variant="outlined" fullWidth as={TextField} />
          <Field type="text" name="name.middle" label="Middle Name :" variant="outlined" fullWidth as={TextField} />
          <Field type="text" name="name.last" label="Last Name :" variant="outlined" fullWidth as={TextField} />
          <FieldArray
            name="emails"
            render={({ push, remove }) => {
              return values.emails.map((el, ind) => (
                <Field
                  key={ind}
                  type="email"
                  name={`emails.${ind}`}
                  variant="outlined"
                  fullWidth
                  as={TextField}
                  InputProps={{
                    endAdornment: (
                      <InputAdornment position="end">
                        <IconButton
                          onClick={(): void => {
                            push('');
                          }}
                        >
                          <AddCircleOutlineIcon />
                        </IconButton>
                        {values.emails.length > 1 && (
                          <IconButton
                            onClick={(): void => {
                              remove(ind);
                            }}
                          >
                            <ClearIcon color="primary" />
                          </IconButton>
                        )}
                      </InputAdornment>
                    ),
                  }}
                />
              ));
            }}
          />
          <Field
            select
            name="gender"
            label="Gender :"
            variant="outlined"
            fullWidth
            as={TextField}
            InputProps={{
              endAdornment: (
                <InputAdornment position="end">
                  <IconButton
                    onClick={(): void => {
                      setFieldValue('gender', '');
                    }}
                  >
                    <ClearIcon />
                  </IconButton>
                </InputAdornment>
              ),
            }}
          >
            {genderOption.map((g) => {
              return (
                <MenuItem key={g.code} value={g.code}>
                  {g.label}
                </MenuItem>
              );
            })}
          </Field>
          <Field
            select
            name="maritalStatus"
            label="Marital Status :"
            variant="outlined"
            fullWidth
            as={TextField}
            InputProps={{
              endAdornment: (
                <InputAdornment position="end">
                  <IconButton
                    onClick={(): void => {
                      setFieldValue('gender', '');
                    }}
                  >
                    <ClearIcon />
                  </IconButton>
                </InputAdornment>
              ),
            }}
          >
            {maritalStatusOption.map((g) => {
              return (
                <MenuItem key={g.code} value={g.code}>
                  {g.label}
                </MenuItem>
              );
            })}
          </Field>

          <pre>{JSON.stringify(values)}</pre>
        </Form>
      )}
    </Formik>
  );
};
export default EditUserProfile;
